import React, { useEffect, useState } from "react";
import { RiAccountPinBoxFill } from "react-icons/ri";
import { SiCashapp } from "react-icons/si";
import { Button, Modal } from "react-bootstrap";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Account = () => {
  let navigate = useNavigate();

  let [position, setPosition] = useState("");

  let currentWidth = document.documentElement.clientWidth;

  useEffect(() => {
    if (currentWidth > 770) {
      setPosition("absolute");
    }
  }, []);

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;

    if (screenWidth <= 770) {
      setPosition("");
    }

    if (screenWidth > 770) {
      setPosition("absolute");
    }
  });

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [idCheck, SetidCheck] = useState(false);
  const [idWarn, SetidWarn] = useState(false);
  const [idSame, SetidSame] = useState(false);

  const [pwCheck, SetpwCheck] = useState(false);
  const [pwWarn, SetpwWarn] = useState(false);

  const [pw2Check, Setpw2Check] = useState(false);
  const [pw2Warn, Setpw2Warn] = useState(false);

  const [nickCheck, SetnickCheck] = useState(false);
  const [nickWarn, SetnickWarn] = useState(false);
  const [nickSame, SetnickSame] = useState(false);

  const idInput = document.getElementById("ID");
  const pwInput = document.getElementById("PW");
  const pw2Input = document.getElementById("PW2");
  const nickInput = document.getElementById("NICK");

  const checkValue = (input) => {
    console.log(input.value);

    return input.value;
  };

  const resetShow = () => {
    SetidCheck(false);
    SetidWarn(false);
    SetidSame(false);
    SetpwCheck(false);
    SetpwWarn(false);
    Setpw2Check(false);
    Setpw2Warn(false);
    SetnickCheck(false);
    SetnickWarn(false);
    SetnickSame(false);
  };

  // resize screen

  let [flexdir, Setflexdir] = useState("row wrap");

  useEffect(() => {
    if (currentWidth > 990) {
      Setflexdir("row wrap");
    }
    if (currentWidth <= 990) {
      Setflexdir("column wrap");
    }
  }, [flexdir]);

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;

    if (screenWidth <= 990) {
      Setflexdir("column wrap");
    }

    if (screenWidth > 990) {
      Setflexdir("row wrap");
    }
  });

  ///end resize screen

  // styled component
  let TopTitle = styled("p")`
    font-size: 48px;
    padding-bottom: 50px;
