import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import { SiCashapp } from "react-icons/si";
import styled from "styled-components";
import { Button, Card } from "react-bootstrap";
import noImage from "../img/noImage.svg";
import { BsFillCaretRightFill } from "react-icons/bs";
import { useSelector } from "react-redux";

const DetailBoard = (props) => {
  let navigate = useNavigate();

  let { bno } = useParams();
  console.log(bno);

  let state = useSelector((state) => {
    return state;
  });

  let boardState = state.boardReducer;

  console.log(boardState[bno - 1]);

  let item = boardState[bno - 1];

  let tags = item.tag;

  let tagsArray = tags.split(",");
  console.log(tagsArray);

  // styled component
  let TopTitle = styled("p")`
    font-size: 48px;
  `;

  let Line = styled("div")`
    width: 80%;
    height: 5px;
    background-color: #2d4059;
    margin: 0 auto;
  `;
  // end styled component
  return (
    <div>
      <div className="cotainer-lg">
        <div
          className="row d-flex justify-content-center"
          style={{ paddingTop: "50px" }}
        >
          <div
            className="col-lg-1"
            onClick={() => {
              navigate("/");
            }}
            style={{ marginBottom: "20px", cursor: "pointer" }}
          >
            <SiCashapp
              style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
            />
          </div>
          <div className="col-lg-9">
            <TopTitle>Wait you feed back !!</TopTitle>
          </div>
        </div>
      </div>
      <Line></Line>
      <div className="container-lg" style={{ marginTop: "30px" }}>
        <div className="row d-flex justify-content-end">
          <div className="col-2 d-flex justify-content-evenly">
            <Button>글수정</Button>
            <Button>글삭제</Button>
          </div>
        </div>
      </div>
      {/* detail card */}
      <div
        className="container-lg d-flex justify-content-center"
        style={{ marginTop: "50px" }}
      >
        <Card style={{ width: "800px", color: "white", padding: "10px" }}>
          <Card.Img
            variant="top"
            src={noImage}
            style={{ border: "2px solid #2D4059" }}
          />
          <Card.Body style={{ padding: "1rem 0" }}>
            <div style={{ display: "flex", justifyContent: "center" }}>
              <Card.Title
                style={{
                  MaxWidth: "700px",
                  height: "40px",
                  textAlign: "left",
                  backgroundColor: "#2D4059",
                  borderRadius: "5px",
                  padding: "10px",
                  overflow: "hidden",
                  textOverflow: "ellipsis",
                  whiteSpace: "nowrap",
                }}
              >
                {item.title}
              </Card.Title>
            </div>
            <div style={{ display: "flex", justifyContent: "center" }}>
              <p
                style={{
                  maxWidth: "700px",
                  height: "40px",
                  backgroundColor: "#2D4059",
                  borderRadius: "5px",
                  padding: "10px",
                  overflow: "hidden",
                  textOverflow: "ellipsis",
                  whiteSpace: "nowrap",
                }}
              >
                {item.writer}
              </p>
            </div>

            <Card.Text
              style={{
                backgroundColor: "#2D4059",
                borderRadius: "5px",
                padding: "10px",
              }}
            >
              {item.content}
            </Card.Text>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <p
                style={{
                  width: "70px",
                  height: "40px",
                  backgroundColor: "#2D4059",
                  borderRadius: "5px",
                  padding: "10px",
                  marginBottom: "0",
                }}
              >
                제시가
              </p>
              <BsFillCaretRightFill
                style={{ color: "#EA5455", fontSize: "32px" }}
              />
              <div
                style={{
                  color: "black",
                  width: "250px",
                  fontSize: "24px",
                  borderBottom: "2px solid #2D4059",
                }}
              >
                {item.suggestion ? item.suggestion + " 원" : "없음"}
              </div>
            </div>
            <div style={{ marginTop: "20px", textAlign: "left" }}>
              {tagsArray.map((item, index) => {
                return (
                  <span
                    style={{
                      display: "inline-block",
                      maxWidth: "700px",
                      height: "40px",
                      backgroundColor: "#2D4059",
                      borderRadius: "5px",
                      padding: "10px",
                      marginBottom: "0",
                      marginRight: "10px",
                      textOverflow: "ellipsis",
                      overflow: "hidden",
                      whiteSpace: "nowrap",
                    }}
                    key={index}
                  >
                    #{item}
                  </span>
                );
              })}
            </div>
          </Card.Body>
        </Card>
      </div>
      {/* end detail card */}
    </div>
  );
};

export default DetailBoard;
