import React, { useEffect, useState } from "react";
import { SiCashapp } from "react-icons/si";
import { Button, Modal } from "react-bootstrap";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Register = () => {
  let navigate = useNavigate();

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [titleCheck, SettitleCheck] = useState(false);

  const [contentCheck, SetcontentCheck] = useState(false);

  const [pw2Check, Setpw2Check] = useState(false);

  const [nickCheck, SetnickCheck] = useState(false);

  const idInput = document.getElementById("ID");
  const pwInput = document.getElementById("PW");
  const pw2Input = document.getElementById("PW2");
  const nickInput = document.getElementById("NICK");

  const checkValue = (input) => {
    console.log(input.value);

    return input.value;
  };

  const resetShow = () => {
    SettitleCheck(false);
    SetidWarn(false);
    SetcontentCheck(false);
    SetpwWarn(false);
    Setpw2Check(false);
    Setpw2Warn(false);
    SetnickCheck(false);
    SetnickWarn(false);
  };

  // styled component
  let TopTitle = styled("p")`
    font-size: 48px;
  `;

  let Label = styled("div")`
    font-size: 48px;
    width: 250px;
  `;

  let Warn = styled("p")`
    color: red;
  `;

  let Line = styled("div")`
    width: 80%;
    height: 5px;
    background-color: #2d4059;
    margin: 0 auto;
  `;
  // end styled component

  return (
    <div>
      <div className="cotainer-lg">
        <div
          className="row d-flex justify-content-center"
          style={{ paddingTop: "50px" }}
        >
          <div
            className="col-lg-1"
            onClick={() => {
              navigate("/");
            }}
            style={{ marginBottom: "20px", cursor: "pointer" }}
          >
            <SiCashapp
              style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
            />
          </div>
          <div className="col-lg-9">
            <TopTitle>Check items & Feed back</TopTitle>
          </div>
        </div>
      </div>
      <Line></Line>
      <div className="container">
        <div className="row">
          <div
            className="col d-flex flex-column justify-content-center align-items-center"
            style={{ paddingTop: "80px" }}
          >
            <div
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
                alignItems: "center",
              }}
            >
              <Label>제목</Label>
              <input
                id="ID"
                type="text"
                placeholder="제목을 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginRight: "20px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </div>
            {titleCheck ? <Warn>이메일을 입력해주세요.</Warn> : null}
            <div
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>
                사진 첨부
                <br />
                (선택사항)
              </Label>
              <div
                style={{
                  width: "500px",
                  height: "400px",
                  marginLeft: "20px",
                  fontSize: "20px",
                  border: "1px solid #666",
                  borderRadius: "3px",
                }}
              >
                <input
                  id="PW"
                  type="file"
                  style={{ width: "450px", margin: "20px 0" }}
                />
              </div>
            </div>
            <div
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>내용</Label>
              <textarea
                id="PW2"
                placeholder="본문 내용을 입력하세요"
                style={{
                  width: "500px",
                  height: "400px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </div>
            {contentCheck ? <Warn>비밀번호를 입력해주세요.</Warn> : null}
            <div
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
                alignItems: "center",
              }}
            >
              <Label>
                제시가
                <br />
                (선택사항)
              </Label>
              <input
                id="NICK"
                type="text"
                placeholder="제시할 가격을 입력하세요 (생략가능)"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "20px",
                  marginRight: "20px",
                  fontSize: "20px",
                }}
              />
            </div>
            {nickCheck ? <Warn>닉네임을 입력해주세요.</Warn> : null}
            <div
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
                alignItems: "center",
              }}
            >
              <Label>태그 (최대3개)</Label>
              <input
                id="NICK1"
                type="text"
                placeholder="태그 입력란"
                style={{
                  width: "150px",
                  height: "50px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
              <input
                id="NICK2"
                type="text"
                placeholder="태그 입력란"
                style={{
                  width: "150px",
                  height: "50px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
              <input
                id="NICK3"
                type="text"
                placeholder="태그 입력란"
                style={{
                  width: "150px",
                  height: "50px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </div>
            <div
              style={{
                paddingTop: "60px",
                paddingRight: "20px",
                display: "flex",
                width: "300px",
                justifyContent: "space-evenly",
                paddingBottom: "50px",
              }}
            >
              <Button
                onClick={() => {
                  resetShow();

                  const korean = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣]/; //한글
                  const specialText = /[~!@#$%<>^&*]/; //특수문자

                  // console.log(korean.test("닉네임"));
                  // console.log(specialText.test("asser@@"));

                  const idInputArray = idInput.value.split("");
                  const nickInputArray = nickInput.value.split("");

                  // console.log(nickInputArray.length);

                  // console.log(idInputArray);
                  // console.log(!idInputArray.find((item) => item == "@"));
                  // console.log(!idInputArray.find((item) => item == "."));

                  if (!checkValue(idInput)) {
                    SettitleCheck(true);
                    return;
                  }
                  if (!checkValue(pwInput)) {
                    SetcontentCheck(true);
                    return;
                  }
                  if (!checkValue(pw2Input)) {
                    Setpw2Check(true);
                    return;
                  }
                  if (!checkValue(nickInput)) {
                    SetnickCheck(true);
                    return;
                  }

                  handleShow();
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                등록하기
              </Button>
              <Button
                onClick={() => {
                  navigate("/");
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                취소
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* modal */}
      <Modal
        show={show}
        onHide={handleClose}
        style={{ fontFamily: "'Do Hyeon', sans-serif" }}
      >
        <Modal.Header closeButton>
          <Modal.Title>글이 정상 등록되었습니다.</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>게시판페이지에서 등록한 게시물을 확인해보세요.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              navigate("/login");
            }}
          >
            로그인 페이지로 이동
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              navigate("/welcome");
            }}
          >
            닫기
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

