import React, { useState } from "react";
import { Card } from "react-bootstrap";
import noImage from "../img/noImage.svg";
import { BsFillCaretRightFill } from "react-icons/bs";

const ItemCard = (props) => {
  let tags = props.item.tag;

  let tagsArray = [...tags.split(",")];
  // console.log(tagsArray);

  let tagtag = [1, 2, 3];
  return (
    <div style={{ margin: "20px auto" }}>
      <Card style={{ width: "300px", color: "white", padding: "10px" }}>
        <Card.Img
          variant="top"
          src={noImage}
          style={{ border: "2px solid #2D4059" }}
        />
        <Card.Body style={{ padding: "1rem 0" }}>
          <div style={{ display: "flex" }}>
            <Card.Title
              style={{
                width: "300px",
                height: "40px",
                textAlign: "left",
                backgroundColor: "#2D4059",
                borderRadius: "5px",
                padding: "10px",
                overflow: "hidden",
              }}
            >
              {props.item.title}
            </Card.Title>
          </div>
          <div style={{ display: "flex" }}>
            <p
              style={{
                maxWidth: "300px",
                height: "40px",
                backgroundColor: "#2D4059",
                borderRadius: "5px",
                padding: "10px",
                overflow: "hidden",
              }}
            >
              {props.item.writer}
            </p>
          </div>

          {/* <Card.Text
            style={{
              height: "150px",
              backgroundColor: "#2D4059",
              borderRadius: "5px",
              padding: "10px",
              overflow: "hidden",
            }}
          >
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </Card.Text> */}
          <div style={{ display: "flex", alignItems: "center" }}>
            <p
              style={{
                width: "70px",
                height: "40px",
                backgroundColor: "#2D4059",
                borderRadius: "5px",
                padding: "10px",
                marginBottom: "0",
              }}
            >
              제시가
            </p>
            <BsFillCaretRightFill
              style={{ color: "#EA5455", fontSize: "32px" }}
            />
            <div
              style={{
                color: "black",
                width: "250px",
                fontSize: "24px",
                borderBottom: "2px solid #2D4059",
              }}
            >
              {props.item.suggestion ? props.item.suggestion + " 원" : "없음"}
            </div>
          </div>
          <div style={{ marginTop: "20px", textAlign: "left" }}>
            {tagsArray.map((item, index) => {
              return (
                <span
                  style={{
                    minWidth: "70px",
                    height: "40px",
                    backgroundColor: "#2D4059",
                    borderRadius: "5px",
                    padding: "10px",
                    marginBottom: "0",
                    marginRight: "10px",
                  }}
                  key={index}
                >
                  #{item}
                </span>
              );
            })}
          </div>
        </Card.Body>
      </Card>
    </div>
  );
};

export default ItemCard;
