import React, { useState } from "react";
import { Container, Nav, Navbar } from "react-bootstrap";
import { useSelector } from "react-redux";
import { SiCashapp } from "react-icons/si";

const Mainpage = () => {
  let state = useSelector((state) => {
    return state;
  });

  let currentScreenWidth = document.documentElement.clientWidth;

  let [imageWidth, imageWidthChange] = useState(currentScreenWidth / 2);

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;
    imageWidthChange(screenWidth);
  });

  let bannerURL =
    "https://doodleipsum.com/" +
    imageWidth +
    "/outline?i=b9394e5dbf605f5eef5ede6fe707ad8c";

  return (
    <div>
      <Navbar
        bg="dark"
        variant="dark"
        expand="lg"
        style={{ backgroundColor: "2D4059" }}
      >
        <Container style={{ maxWidth: "1400px" }}>
          <div className="d-flex align-items-center">
            <SiCashapp
              style={{ color: "EA5455", fontSize: "70px", margin: "0 20px" }}
            />
            <Navbar.Brand
              href="#home"
              style={{
                color: "white",
                fontSize: "50px",
                paddingTop: "15px",
                fontWeight: "bold",
              }}
            >
              How Much ?
            </Navbar.Brand>
          </div>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="fixnav basic-navbar-nav">
            <Nav
              className="d-flex justify-content-around"
              style={{ width: "400px" }}
            >
              <Nav.Link
                href="#home"
                style={{ color: "white", fontSize: "24px" }}
              >
                로그인
              </Nav.Link>
              <Nav.Link
                href="#features"
                style={{ color: "white", fontSize: "24px" }}
              >
                회원가입
              </Nav.Link>
              <Nav.Link
                href="#pricing"
                style={{ color: "white", fontSize: "24px" }}
              >
                마이 포인트
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div id="jumbo">
        <div>
          <img src={bannerURL} alt="Communication by Karthik Srinivas" />
        </div>
      </div>
    </div>
  );
};

export default Mainpage;
