import React, { useState } from "react";
import { Button, Container, Nav, Navbar } from "react-bootstrap";
import { useSelector } from "react-redux";
import { SiCashapp } from "react-icons/si";
import styled from "styled-components";
import Banner from "../img/banner.svg";
import ItemCard from "./ItemCard";

const Mainpage = () => {
  let state = useSelector((state) => {
    return state;
  });

  // let currentImageWidth = document.documentElement.clientWidth;

  let [imageWidth, imageWidthChange] = useState("600px");

  let [rowAlign, rowAlignChange] = useState("left");

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;

    if (screenWidth <= 990) {
      imageWidthChange("450px");
      rowAlignChange("center");
    }

    if (screenWidth > 990) {
      imageWidthChange("600px");
      rowAlignChange("left");
    }
  });

  console.log(imageWidth);

  let Jumbo = styled("div")`
    background-color: #ffd460;
  `;

  let Line = styled("div")`
    border-bottom: 3px solid #2d4059;
    margin: 150px auto;
  `;

  let GuideText = styled("div")`
    text-align: left;
  `;

  return (
    <div>
      {/* navbar */}
      <Navbar
        bg="dark"
        variant="dark"
        expand="lg"
        style={{ backgroundColor: "#2D4059" }}
      >
        <Container style={{ maxWidth: "1400px" }}>
          <div className="d-flex align-items-center">
            <SiCashapp
              style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
            />
            <Navbar.Brand
              href="#home"
              style={{
                color: "white",
                fontSize: "50px",
                paddingTop: "15px",
                fontWeight: "bold",
              }}
            >
              How Much ?
            </Navbar.Brand>
          </div>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="fixnav basic-navbar-nav">
            <Nav
              className="d-flex justify-content-around"
              style={{ width: "400px" }}
            >
              <Nav.Link
                href="#home"
                style={{ color: "white", fontSize: "24px" }}
              >
                로그인
              </Nav.Link>
              <Nav.Link
                href="#features"
                style={{ color: "white", fontSize: "24px" }}
              >
                회원가입
              </Nav.Link>
              <Nav.Link
                href="#pricing"
                style={{ color: "white", fontSize: "24px" }}
              >
                마이 포인트
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* jumbotron */}
      <Jumbo>
        <div
          style={{
            position: "relative",
            paddingTop: "100px",
            paddingBottom: "150px",
          }}
        >
          <p
            style={{
              fontSize: "24px",
              position: "absolute",
              top: "50px",
              left: "50px",
            }}
          >
            당신이 가진 어떤 것도{" "}
            <span style={{ borderBottom: "2px solid #EA5455" }}>
              재미있는 가치
            </span>
            가 된다 !
          </p>
          <img src={Banner} alt="banner" style={{ width: imageWidth }} />
          <div
            style={{
              position: "absolute",
              bottom: "10px",
              left: "50px",
              textAlign: "left",
            }}
          >
            <p style={{ fontSize: "24px" }}>
              모든 컨텐츠는 결국 <span style={{ color: "#EA5455" }}>사람</span>
              이니까,
            </p>
            <p style={{ fontSize: "24px" }}>
              당신만의 무언가에{" "}
              <span style={{ borderBottom: "2px solid #EA5455" }}>
                가치를 매겨보세요 !
              </span>
            </p>
            <Button style={{ backgroundColor: "#EA5455", border: "none" }}>
              시작하기
            </Button>
          </div>
        </div>
      </Jumbo>

      {/* item list */}

      <div className="container-lg" style={{ margin: "80px auto" }}>
        <div
          className="row"
          style={{
            margin: "30px 0",
            fontSize: "32px",
            textAlign: rowAlign,
          }}
        >
          <span>인기 평가물</span>
        </div>
        <div className="row">
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
        </div>
      </div>
      <div className="container-lg" style={{ margin: "80px auto" }}>
        <div
          className="row"
          style={{
            margin: "30px 0",
            fontSize: "32px",
            textAlign: rowAlign,
          }}
        >
          <span>새 평가물</span>
        </div>
        <div className="row">
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
          <div className="col-lg-4 d-flex justify-content-center">
            <ItemCard />
          </div>
        </div>
      </div>

      <Line className="container-lg"></Line>

      {/* user guide */}
      <div className="container-lg" style={{ marginBottom: "100px" }}>
        <div className="row" style={{ margin: "50px auto" }}>
          <span style={{ fontSize: "32px" }}>
            우리의 관심사는 당신의 상상력 넘치는{" "}
            <span style={{ color: "#F07B3F" }}>모든 것</span>
          </span>
        </div>
        <div className="row" style={{ marginTop: "50px" }}>
          <div className="col-lg-5">
            <GuideText>
              <p>
                상상력 넘치는 아이디어, 웃긴 사진, 자신만의 짤, 유용한 정보글
                혹은 실제 자신의 평가 받고 싶은 물건까지 ... !
              </p>
              <p>전부 포스팅하여 가치를 알아보고 실제 포인트로 받아보세요 !</p>
            </GuideText>
          </div>
          <div className="col-lg-5"></div>
        </div>
      </div>
    </div>
  );
};

export default Mainpage;
