import React, { useEffect, useState } from "react";
import { RiAccountPinBoxFill } from "react-icons/ri";
import { SiCashapp } from "react-icons/si";
import { Button, Modal } from "react-bootstrap";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Account = () => {
  let navigate = useNavigate();

  let [position, setPosition] = useState("");

  let currentWidth = document.documentElement.clientWidth;

  useEffect(() => {
    if (currentWidth > 770) {
      setPosition("absolute");
    }
  }, []);

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;

    if (screenWidth <= 770) {
      setPosition("");
    }

    if (screenWidth > 770) {
      setPosition("absolute");
    }
  });

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [idShow, SetidShow] = useState(false);
  const [idCheck, SetidCheck] = useState(false);

  const [pwShow, SetpwShow] = useState(false);

  const [nickShow, SetnickShow] = useState(false);
  const [nickCheck, SetnickCheck] = useState(false);

  const idInput = document.getElementById("ID");
  const pwInput = document.getElementById("PW");
  const nickInput = document.getElementById("NICK");

  // styled component
  let TopTitle = styled("p")`
    font-size: 48px;
    padding-bottom: 50px;
  `;

  let Head = styled("p")`
    font-size: 32px;
    width: 300px;
    border-bottom: 2px solid #2d4059;
  `;

  let Label = styled("span")`
    font-size: 48px;
  `;

  let Warn = styled("p")`
    color: red;
  `;
  // end styled component

  return (
    <div>
      <div className="container" style={{ position: "relative" }}>
        {/* logo */}
        <div
          onClick={() => {
            navigate("/");
          }}
          style={{
            position: position,
            paddingTop: "80px",
            left: "80px",
            cursor: "pointer",
          }}
        >
          <SiCashapp
            style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
          />
        </div>
        {/* end logo */}

        <div className="row">
          <div
            className="col d-flex flex-column justify-content-center align-items-center"
            style={{ paddingTop: "80px" }}
          >
            <TopTitle>
              Join us
              <br />&<br />
              Make your Value !!
            </TopTitle>
            <Head>Create Account</Head>
            <p>
              <RiAccountPinBoxFill style={{ fontSize: "48px" }} />
            </p>
            <p
              style={{
                display: "flex",
                width: "770px",
                alignItems: "center",
                paddingTop: "30px",
              }}
            >
              <Label>ID(Email)</Label>
              <input
                id="ID"
                type="text"
                placeholder="아이디를 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginRight: "20px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
              <Button>중복확인</Button>
            </p>
            {idShow ? (
              <Warn>올바른 형식의 이메일이 아닙니다. 다시 입력해주세요.</Warn>
            ) : null}
            {idCheck ? (
              <Warn>
                중복되는 아이디가 존재합니다. 다른 이메일을 입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "600px",
                alignItems: "center",
                paddingTop: "30px",
              }}
            >
              <Label>PW</Label>
              <input
                id="PW"
                type="password"
                placeholder="비밀번호를 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "40px",
                  fontSize: "20px",
                }}
              />
            </p>
            {pwShow ? (
              <Warn>올바른 형식의 비밀번호가 아닙니다. 다시 입력해주세요.</Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "650px",
                alignItems: "center",
                paddingTop: "30px",
              }}
            >
              <Label>닉네임</Label>
              <input
                id="NICK"
                type="text"
                placeholder="닉네임을 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "22px",
                  marginRight: "20px",
                  fontSize: "20px",
                }}
              />
              <Button>중복확인</Button>
            </p>
            {nickShow ? (
              <Warn>닉네임 제한길이를 초과하였습니다. 다시 입력해주세요.</Warn>
            ) : null}
            {nickCheck ? (
              <Warn>
                중복되는 닉네임이 존재합니다. 다른 닉네임을 입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                paddingTop: "60px",
                paddingRight: "20px",
                display: "flex",
                width: "300px",
                justifyContent: "space-evenly",
                paddingBottom: "50px",
              }}
            >
              <Button
                onClick={() => {
                  handleShow();
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                가입하기
              </Button>
              <Button
                onClick={() => {
                  navigate("/");
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                취소
              </Button>
            </p>
          </div>
        </div>
      </div>

      {/* modal */}
      <Modal
        show={show}
        onHide={handleClose}
        style={{ fontFamily: "'Do Hyeon', sans-serif" }}
      >
        <Modal.Header closeButton>
          <Modal.Title>환영합니다!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>가입이 정상적으로 완료되었습니다.</p>
          <p>로그인페이지에서 가입한 아이디로 로그인 하세요.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              navigate("/login");
            }}
          >
            로그인 페이지로 이동
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              navigate("/welcome");
            }}
          >
            닫기
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Account;
