import React, { useEffect, useState } from "react";
import { RiAccountPinBoxFill } from "react-icons/ri";
import { SiCashapp } from "react-icons/si";
import { Button, Modal } from "react-bootstrap";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Register = () => {
  let navigate = useNavigate();

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [idCheck, SetidCheck] = useState(false);
  const [idWarn, SetidWarn] = useState(false);
  const [idSame, SetidSame] = useState(false);

  const [pwCheck, SetpwCheck] = useState(false);
  const [pwWarn, SetpwWarn] = useState(false);

  const [pw2Check, Setpw2Check] = useState(false);
  const [pw2Warn, Setpw2Warn] = useState(false);

  const [nickCheck, SetnickCheck] = useState(false);
  const [nickWarn, SetnickWarn] = useState(false);
  const [nickSame, SetnickSame] = useState(false);

  const idInput = document.getElementById("ID");
  const pwInput = document.getElementById("PW");
  const pw2Input = document.getElementById("PW2");
  const nickInput = document.getElementById("NICK");

  const checkValue = (input) => {
    console.log(input.value);

    return input.value;
  };

  const resetShow = () => {
    SetidCheck(false);
    SetidWarn(false);
    SetidSame(false);
    SetpwCheck(false);
    SetpwWarn(false);
    Setpw2Check(false);
    Setpw2Warn(false);
    SetnickCheck(false);
    SetnickWarn(false);
    SetnickSame(false);
  };

  // styled component
  let TopTitle = styled("p")`
    font-size: 48px;
  `;

  let Label = styled("span")`
    font-size: 48px;
    width: 250px;
  `;

  let Warn = styled("p")`
    color: red;
  `;

  let Line = styled("div")`
    width: 80%;
    height: 5px;
    background-color: #2d4059;
    margin: 0 auto;
  `;
  // end styled component

  return (
    <div>
      <div className="cotainer-lg">
        <div
          className="row d-flex justify-content-center"
          style={{ paddingTop: "50px" }}
        >
          <div
            className="col-lg-1"
            onClick={() => {
              navigate("/");
            }}
            style={{ marginBottom: "20px", cursor: "pointer" }}
          >
            <SiCashapp
              style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
            />
          </div>
          <div className="col-lg-9">
            <TopTitle>Check items & Feed back</TopTitle>
          </div>
        </div>
      </div>
      <Line></Line>
      <div className="container">
        <div className="row">
          <div
            className="col d-flex flex-column justify-content-center align-items-center"
            style={{ paddingTop: "80px" }}
          >
            <p
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>제목</Label>
              <input
                id="ID"
                type="text"
                placeholder="제목을 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginRight: "20px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </p>
            {idCheck ? <Warn>이메일을 입력해주세요.</Warn> : null}
            {idWarn ? (
              <Warn>올바른 형식의 이메일이 아닙니다. 다시 입력해주세요.</Warn>
            ) : null}
            {idSame ? (
              <Warn>
                중복되는 아이디가 존재합니다. 다른 이메일을 입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>
                사진 첨부
                <br />
                (선택사항)
              </Label>
              <input
                id="PW"
                type="password"
                placeholder="비밀번호를 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </p>
            {pwCheck ? <Warn>비밀번호를 입력해주세요.</Warn> : null}
            {pwWarn ? (
              <Warn>
                올바른 형식의 비밀번호가 아닙니다. 특수문자를 넣어서 다시
                입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>내용</Label>
              <textarea
                id="PW2"
                placeholder="본문 내용을 입력하세요"
                style={{
                  width: "400px",
                  height: "400px",
                  marginLeft: "20px",
                  fontSize: "20px",
                }}
              />
            </p>
            {pw2Check ? <Warn>비밀번호를 입력해주세요.</Warn> : null}
            {pw2Warn ? (
              <Warn>
                입력했던 비밀번호와 일치하지 않습니다. 다시 입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>
                제시가
                <br />
                (선택사항)
              </Label>
              <input
                id="NICK"
                type="text"
                placeholder="닉네임을 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "20px",
                  marginRight: "20px",
                  fontSize: "20px",
                }}
              />
            </p>
            {nickCheck ? <Warn>닉네임을 입력해주세요.</Warn> : null}
            {nickWarn ? (
              <Warn>닉네임 제한길이를 초과하였습니다. 다시 입력해주세요.</Warn>
            ) : null}
            {nickSame ? (
              <Warn>
                중복되는 닉네임이 존재합니다. 다른 닉네임을 입력해주세요.
              </Warn>
            ) : null}
            <p
              style={{
                display: "flex",
                width: "770px",
                paddingTop: "30px",
              }}
            >
              <Label>태그 (최대3개)</Label>
              <input
                id="NICK"
                type="text"
                placeholder="닉네임을 입력하세요"
                style={{
                  width: "400px",
                  height: "50px",
                  marginLeft: "20px",
                  marginRight: "20px",
                  fontSize: "20px",
                }}
              />
            </p>
            <p
              style={{
                paddingTop: "60px",
                paddingRight: "20px",
                display: "flex",
                width: "300px",
                justifyContent: "space-evenly",
                paddingBottom: "50px",
              }}
            >
              <Button
                onClick={() => {
                  resetShow();

                  const korean = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣]/; //한글
                  const specialText = /[~!@#$%<>^&*]/; //특수문자

                  // console.log(korean.test("닉네임"));
                  // console.log(specialText.test("asser@@"));

                  const idInputArray = idInput.value.split("");
                  const nickInputArray = nickInput.value.split("");

                  // console.log(nickInputArray.length);

                  // console.log(idInputArray);
                  // console.log(!idInputArray.find((item) => item == "@"));
                  // console.log(!idInputArray.find((item) => item == "."));

                  if (!checkValue(idInput)) {
                    SetidCheck(true);
                    return;
                  }
                  if (!checkValue(pwInput)) {
                    SetpwCheck(true);
                    return;
                  }
                  if (!checkValue(pw2Input)) {
                    Setpw2Check(true);
                    return;
                  }
                  if (!checkValue(nickInput)) {
                    SetnickCheck(true);
                    return;
                  }
                  if (
                    !idInputArray.find((item) => item === "@") ||
                    !idInputArray.find((item) => item === ".")
                  ) {
                    SetidWarn(true);
                    idInput.value = "";
                    return;
                  }

                  if (!specialText.test(pwInput.value)) {
                    SetpwWarn(true);
                    pwInput.value = "";
                    return;
                  }

                  if (!(pwInput.value === pw2Input.value)) {
                    Setpw2Warn(true);
                    pw2Input.value = "";
                    return;
                  }
                  if (korean.test(nickInput.value)) {
                    if (nickInputArray.length > 15) {
                      SetnickWarn(true);
                      nickInput.value = "";
                      return;
                    }
                  } else {
                    if (nickInputArray.length > 30) {
                      SetnickWarn(true);
                      nickInput.value = "";
                      return;
                    }
                  }

                  handleShow();
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                가입하기
              </Button>
              <Button
                onClick={() => {
                  navigate("/");
                }}
                style={{
                  backgroundColor: "#2d4059",
                  border: "none",
                  fontSize: "24px",
                }}
              >
                취소
              </Button>
            </p>
          </div>
        </div>
      </div>

      {/* modal */}
      <Modal
        show={show}
        onHide={handleClose}
        style={{ fontFamily: "'Do Hyeon', sans-serif" }}
      >
        <Modal.Header closeButton>
          <Modal.Title>환영합니다!</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>가입이 정상적으로 완료되었습니다.</p>
          <p>로그인페이지에서 가입한 아이디로 로그인 하세요.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              navigate("/login");
            }}
          >
            로그인 페이지로 이동
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              navigate("/welcome");
            }}
          >
            닫기
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Register;
