                url: "/RegisterValue",
                method: "POST",
                data: {
                  bno: item.bno,
                  price: price.value,
                },
              }).then((res) => {
                console.log(res.data[0].price === -1);
                if (res.data === "") {
                  console.log("same user");
                  SetwarnDuplication(true);
                  readValueList();
                  handleWarnClose();
                } else if (res.data[0].price === -1) {
                  console.log("자신의 게시물임");
                  SetwarnSelfValue(true);
                  readValueList();
                  handleWarnClose();
                } else {
                  console.log(res.data);
                  dispatch({
                    type: "valuelog",
                    payload: res.data,
                  });
                }
              });

              await readCalculateValue();

              price.value = "";

              handleWarnClose();
            }}
          >
            평가확정
          </Button>
        </Modal.Footer>
      </Modal>
      {/* warn value modal end */}

      {/* login modal start */}
      <Modal
        show={loginShow}
        onHide={handleLoginShow}
        style={{ fontFamily: "'Do Hyeon', sans-serif" }}
      >
        <Modal.Header>
          <Modal.Title>⚠️ 로그인이 필요한 서비스입니다! ⚠️</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          평가를 하시려면 로그인이 필요합니다.
          <br />
          로그인을 하시거나 회원이 아니시라면 회원가입 후 이용 부탁드립니다.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleLoginClose}>
            취소
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              handleLoginClose();
              navigate("/login");
            }}
          >
            로그인하러 가기
          </Button>
        </Modal.Footer>
      </Modal>
      {/* login modal end */}

      {/* value end button  start*/}

      {/* value end button  end*/}
    </div>
  );
};

export default DetailBoard;
