import React, { useState } from "react";
import { Container, Nav, Navbar } from "react-bootstrap";
import { useSelector } from "react-redux";
import { SiCashapp } from "react-icons/si";
import styled from "styled-components";
import { relative } from "path/posix";

const Mainpage = () => {
  let state = useSelector((state) => {
    return state;
  });

  let [bigBannerOnOff, bigBannerOnOffChange] = useState("block");

  let [smallBannerOnOff, smallBannerOnOffChange] = useState("none");

  window.addEventListener("resize", () => {
    let screenWidth = document.documentElement.clientWidth;

    if (screenWidth <= 990) {
      bigBannerOnOffChange("none");
      smallBannerOnOffChange("block");
    }

    if (screenWidth > 990) {
      bigBannerOnOffChange("block");
      smallBannerOnOffChange("none");
    }
  });

  let Jumbo = styled("div")`
    background-color: #ffd460;
  `;

  return (
    <div>
      <Navbar
        bg="dark"
        variant="dark"
        expand="lg"
        style={{ backgroundColor: "#2D4059" }}
      >
        <Container style={{ maxWidth: "1400px" }}>
          <div className="d-flex align-items-center">
            <SiCashapp
              style={{ color: "#EA5455", fontSize: "70px", margin: "0 20px" }}
            />
            <Navbar.Brand
              href="#home"
              style={{
                color: "white",
                fontSize: "50px",
                paddingTop: "15px",
                fontWeight: "bold",
              }}
            >
              How Much ?
            </Navbar.Brand>
          </div>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="fixnav basic-navbar-nav">
            <Nav
              className="d-flex justify-content-around"
              style={{ width: "400px" }}
            >
              <Nav.Link
                href="#home"
                style={{ color: "white", fontSize: "24px" }}
              >
                로그인
              </Nav.Link>
              <Nav.Link
                href="#features"
                style={{ color: "white", fontSize: "24px" }}
              >
                회원가입
              </Nav.Link>
              <Nav.Link
                href="#pricing"
                style={{ color: "white", fontSize: "24px" }}
              >
                마이 포인트
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Jumbo>
        <div style={{ display: bigBannerOnOff, position: "relative" }}>
          <img
            src="https://doodleipsum.com/700/outline?i=b9394e5dbf605f5eef5ede6fe707ad8c"
            alt="Communication by Karthik Srinivas"
          />
        </div>
        <div style={{ display: smallBannerOnOff }}>
          <img
            src="https://doodleipsum.com/450/outline?i=b9394e5dbf605f5eef5ede6fe707ad8c"
            alt="Communication by Karthik Srinivas"
          />
        </div>
      </Jumbo>
    </div>
  );
};

export default Mainpage;
